Let

onmessage = e => {
    if (e.data.op === 'add') {
        let ws = new WebSocket("ws://localhost:3000");
        ws.onopen = () => {
            console.log("open connection");
            ws.send(JSON.stringify(e.data.value));
        };
        ws.onmessage = msg => {
            console.log("getMessage", msg.data);
            postMessage(msg.data);
        };
    }
}
