export const sendToServer = async (obj) => {
    let ws = new WebSocket("ws://localhost:3000");

    ws.onopen = () => {
      console.log("open connection");
      ws.send(JSON.stringify(obj));
    };
    ws.onmessage = msg => {
      console.log("getMessage", msg.data);
    };
    return msg.data;
}
