import React, { useState } from 'react';

import classes from './Chat.module.css';

import { useLiveQuery } from "dexie-react-hooks";

import db from './Dexie/db';

import worker from 'workerize-loader!./worker'; // eslint-disable-line import/no-webpack-loader-syntax

const Chat = () => {
    const [inputVal, setInputVal] = useState('');

    const workerInstance = worker();

    const inputOnChange = (e) => {
        setInputVal(e.target.value);
    }

    const handleKey = (event) => {
        if (event.code === "Enter" || event.code === "NumpadEnter") {
            event.preventDefault();
            addComments(inputVal);
            setInputVal('');
        }
    }

    const addComments = async (text) => {
        await db.comments.add({
            text: text
        }).catch((err)=> {
            window.alert(err);
        })
    }
    // db.comments.clear()

    workerInstance.add(2, 3).then(result => {
        console.log(result)
    })

    const comments = useLiveQuery(
        () => db.comments.toArray()
    );
    console.log(comments)

    return(
        <div className={classes.wrapper}>
            <div className={classes.chat_area}>
                {
                    comments ?
                    comments.map((a)=> {
                        return(
                            <h4 key = {a.id}>{a.text}</h4>
                        );
                    })
                    : null
                }
            </div>
            <input type="text" className={classes.input_field} onChange={(e)=> inputOnChange(e)} onKeyPress = {(e)=> handleKey(e)} value={inputVal} />
        </div>
    );
}

export default Chat;
