import './App.css';
import Chat from './Chat';
import { useState, useEffect } from 'react';
import Login from './Login';
import Axios from 'axios';

function App() {
  const [isAuth, setIsAuth] = useState(false);

  const loginHandler = (name, pass) => {
    Axios({
      method: "POST",
      data: {
        username: name,
        password: pass
      },
      withCredentials: true,
      url: "http://localhost:3000/login"
    }).then((res) => {
      console.log(res)
      if(res.data === 'chat') {
        setIsAuth(true);
      }
    })
  }

  const logOutHandler = () => {
    Axios({
      method: "Get",
      withCredentials: true,
      url: "http://localhost:3000/logout"
    }).then((res) => {
      setIsAuth(false);
      console.log(res)
    })
  }

  useEffect(() => {
    Axios({
      method: "GET",
      withCredentials: true,
      url: "http://localhost:3000/chat",
    }).then((res) => {
      console.log(res.data.authe)
      console.log(res.data);
    }).catch((err)=> {
      console.log(err)
    });
  }, [])

  return (
    <div className="App">
      {
        isAuth ?
        <Chat logOutClicked = {logOutHandler} />
        : <Login loginClicked = {loginHandler} />
      }
    </div>
  );
}

export default App;
